<?php namespace App\Services\Kortextools\Handlers; use App\Contracts\ToolHandlerInterface; class SlugGenHandler implements ToolHandlerInterface { public function handle(array $data): array { $text = $data['text'] ?? ''; $slug = strtolower(trim(preg_replace('/[^a-zA-Z0-9]+/', '-', $text), '-')); return ['success' => true, 'result' => $slug]; } public function getValidationRules(): array { return ['text' => 'required|string']; } public function getTemplate(): string { return 'africoders.kortextools.tools.slug-generator'; } }
